INSERT INTO `tbl_seller_product_reviews` (`spreview_id`, `spreview_seller_user_id`, `spreview_order_id`, `spreview_product_id`, `spreview_selprod_id`, `spreview_selprod_code`, `spreview_postedby_user_id`, `spreview_title`, `spreview_description`, `spreview_posted_on`, `spreview_status`, `spreview_lang_id`) VALUES
(1, 6, 'O1500976706', 42, 89, '42_161', 10, 'Great!!', 'The toy was perfect - just what I was looking for. The item was very reasonably priced, they arrived in perfect condition, and earlier than promised. Importantly, the toy looked exactly like their online photo and description. A hassle-free buying experience!', '2017-07-25 17:28:35', 1, 1),
(2, 5, 'O1500983601', 5, 30, '5_46_51', 10, 'Good!!!', 'Improved front and rear cameras -- now with optical image stabilization -- deliver much improved photos, especially in low light. Water resistant. A faster processor, plus slightly better battery life.', '2017-07-25 17:35:23', 1, 1),
(3, 4, 'O1500976706', 12, 44, '12_', 10, 'Happy!!', 'Sleek, attractive design; Decent gaming and overall performance; Affordable.', '2017-07-25 17:37:18', 1, 1),
(4, 5, 'O1500976706', 36, 103, '36_143', 10, 'Not really happy!!', 'I am not entirely satisfied with the products.Colors are dull and few of the parts are damaged. I feel like all the items I received were seconds but at first grade prices.', '2017-07-25 17:42:46', 1, 1),
(5, 6, 'O1500976455', 38, 98, '38_147', 10, 'Very GOOD!!', 'Absolutely loved the professional and personal service. Fast, efficient and fantastic support. Great recommendation and really love my purchase! Will definitely be buying more as gifts.', '2017-07-25 17:44:57', 1, 1),
(6, 6, 'O1500971470', 40, 95, '40_154', 8, 'Almost Perfect!!', 'These work surprisingly well.I loved the neon sketches. And the light colors -the favourite ones.They work perfectly on any paper without any marks on sheet.With such cool colors a love the connectors too.', '2017-07-25 17:54:43', 1, 1),
(7, 5, 'O1500975757', 8, 13, '8_', 8, 'It\'s Fine!!', 'Cute and sort material.', '2017-07-25 17:57:13', 1, 1),
(8, 6, 'O1500975757', 40, 93, '40_153', 8, 'Satisfied!!', 'Order was late but product was completely fine ......I loved the minion box and sketches work very good.', '2017-07-25 17:59:41', 1, 1),
(9, 5, 'O1500975529', 7, 16, '7_65', 8, 'Not Bad!!', 'I like the product it is fine and fabric was good but design was not exactly the same and the delivery of the product was late.', '2017-07-25 18:03:43', 1, 1),
(10, 6, 'O1500974298', 42, 88, '42_159', 8, 'Impressed!!', 'Shipping was late :(  but product was exactly the same as pictures.....fabric, color , everything was perfect.', '2017-07-25 18:05:55', 1, 1),
(11, 5, 'O1553691436', 23, 58, '23_', 17, 'test', 'https://www.amazon.in/Set-Wet-Deodorant-Perfume-Avatar/dp/B079Q17WXZ?pf_rd_p=2d40a078-39fe-5e38-9093-6dd20fb12f68&pf_rd_r=RXVJD85HPNJD3GWPQF3F&pd_rd_wg=VrIuO&ref_=pd_gw_ri&pd_rd_w=43XaQ&pd_rd_r=bbdca10e-9dde-442b-b894-41b38d0877d5', '2019-07-17 13:13:29', 1, 1),
(12, 5, 'O1553691436', 10, 19, '10_91', 17, 'test', 'https://www.amazon.in/Set-Wet-Perfume-Spunky-Avatar/dp/B079PSW614/ref=pd_bxgy_194_2/262-7763223-7596802?_encoding=UTF8&pd_rd_i=B079PSW614&pd_rd_r=8a808664-a866-11e9-8a72-7dc6102b5f99&pd_rd_w=Kjihu&pd_rd_wg=eTwwv&pf_rd_p=92c5dc59-6da4-488e-ba00-262537223f6b&pf_rd_r=T11JQMKW1FRN82XDVAP9&psc=1&refRID=T11JQMKW1FRN82XDVAP9', '2019-07-17 13:14:59', 1, 1),
(13, 6, 'O1571386744', 68, 159, '68_', 21, 'Great Dress', 'Great Dress but does not work for the occasion I needed it for. Sad to have it returned :(', '2019-10-18 13:52:32', 0, 1),
(14, 11, 'O1571385910', 72, 167, '72_', 21, 'Just Brilliant', 'WOW !! Love the speed and the awesomeness of the product !! Been waiting forever to get my hand on this baby !!', '2019-10-18 13:53:33', 1, 1),
(15, 4, 'O1608711589', 76, 172, '76_49_192', 20, '99.99% Perfect Smartphone !!', 'Another beauty from Apple. I was upgraded from iPhone X and it was great experience with all the latest specs and flawlessly smooth. Thanks to all new A14 Bionic chip.\r\nBuild quality was great as always from Apple.', '2020-12-23 13:51:53', 1, 1),
(16, 4, 'O1608711781', 76, 172, '76_49_192', 21, 'WHAT an upgrade from iPhone 8! Feels amazing in the hand! Like a bar of glass!', 'This was my first switch to an ios device after using an android untill now. And I am not disappointed. The build quality, the responsive touch, the display, the camera, the battery life are just exceptional.\r\nBeen using for about 10 days now, and consistently giving around 7h of screen on time.\r\nThe camera is just way too good. Espeecially the night mode impressed me a lot.\r\nOnly issue when switching from Android to iPhone I encountered was to be not able to restore my whatsapp chats. There are no official/free ways for doing this.\r\nThe phone is simple amazing.', '2020-12-23 13:55:05', 1, 1);
